mapper.update:
UPDATE IDMS_ACCESS_LOG
SET LOGOUT_DTTM = #{logoutDttm}
WHERE USERNAME = #{username}
AND USER_IP = #{userIp}
AND LOGIN_DTTM < #{logoutDttm}
AND LOGOUT_DTTM IS NULL
ORDER BY LOGIN_DTTM DESC
LIMIT 1

