mapper.select:
select mbr_id
, ocbcom_lgn_id
, ci_no
, card_no
, syw_mbr_id
, evs_mbr_id
from mbrt_mbr_id_mst


		and mbr_id = #{mbrId}


		and ocbcom_lgn_id = #{ocbcomLgnId}


		and ci_no = #{ciNo}


		and mbr_kor_nm = #{mbrKorNm}


		and card_no = #{cardNo}


		and syw_mbr_id = #{sywMbrId}


		and evs_mbr_id = #{evsMbrId}
