mapper.sql:
SELECT BASE_YM , UNITED_ID , MBR_ID , MBR_KOR_NM , CLPHN_NO , EMAIL_ADDR , EXTNCT_OBJ_DT FROM ( SELECT TO_CHAR(TO_DATE(BASE_YM, 'YYYYMM'), 'YYYY-MM') AS BASE_YM , UNITED_ID , MBR_ID /* 회원ID */ , MBR_KOR_NM /* 고객성명 */ , CLPHN_NO /* 핸드폰 */ , EMAIL_ADDR /* 이메일 */ , EXTNCT_OBJ_DT /* 소멸대상일자 */ , CASE WHEN B.UNITED_ID IS NOT NULL THEN 1 ELSE 2 END AS OCBCOM /* OCB.com 고객 */ , CASE WHEN B.EMAIL_ADDR IS NOT NULL THEN 1 ELSE 2 END AS EM /* EM 고객 */ , CASE WHEN B.CLPHN_NO IS NOT NULL AND B.CLPHN_CORCT_FG_CD IN ('1','3') THEN 1 ELSE 2 END AS SMS /* SMS고객 */ , CASE WHEN (B.CLPHN_NO IS NOT NULL AND B.CLPHN_CORCT_FG_CD IN ('1','3')) OR B.JOBP_TEL_NO IS NOT NULL OR B.HOME_TEL_NO IS NOT NULL THEN 1 ELSE 2 END AS TM /* TM고객 */ FROM MART_EXTNCT_MBR_NOTI_MST B
AND B.BASE_YM = #{baseYm} /* 조회년월 */
AND B.UNITED_ID = #{unitedId}
AND B.MBR_KOR_NM = #{mbrKorNm}
AND B.CLPHN_NO = #{clphnNo}
AND B.EMAIL_ADDR = #{emailAddr}
) T
AND OCBCOM = 1 AND EM = 1 AND SMS = 1 AND TM = 1
ORDER BY BASE_YM DESC
