mapper.select:
SELECT MBR_ID -- 회원ID ,UNITED_ID -- UNITED_ID
,DECODE(EMAIL_ADDR,NULL,EMAIL_ADDR,SUBSTR(regexp_substr(EMAIL_ADDR, '[^@]+', 1, 1),1,LENGTH(regexp_substr(EMAIL_ADDR, '[^@]+', 1, 1))-2)||'**@'||regexp_substr(EMAIL_ADDR, '[^@]+', 1, 2)) AS EMAIL_ADDR -- 이메일주소
,EMAIL_ADDR
,EMAIL_ADDR_DUP_YN -- 이메일주소중복여부 -- ,TO_CHAR(TO_DATE(MART_UPD_DTTM, 'YYYYMMDD'), 'YYYY-MM-DD') AS MART_UPD_DTTM -- 마트변경일시 ,MART_UPD_DTTM -- 마트변경일시 FROM MART_INDIV_MBR_MST
WHERE EMAIL_ADDR = #{emailAddr}
WHERE EMAIL_ADDR LIKE #{emailAddr}

SELECT MBR_ID -- 회원ID ,UNITED_ID -- UNITED_ID
,'**'||EMAIL_ADDR AS EMAIL_ADDR
,EMAIL_ADDR
,EMAIL_ADDR_DUP_YN -- 이메일주소중복여부 ,TO_CHAR(TO_DATE(MART_UPD_DTTM, 'YYYYMMDD'), 'YYYY-MM-DD') AS MART_UPD_DTTM -- 마트변경일시 FROM MART_INDIV_MBR_MST
WHERE EMAIL_ADDR = #{emailAddr}
WHERE EMAIL_ADDR LIKE #{emailAddr}

