,EMAIL_ADDR
,EMAIL_ADDR_DUP_YN -- 이메일주소중복여부 -- ,TO_CHAR(TO_DATE(MART_UPD_DTTM, 'YYYYMMDD'), 'YYYY-MM-DD') AS MART_UPD_DTTM -- 마트변경일시 ,MART_UPD_DTTM -- 마트변경일시 FROM MART_MKTNG_MBR_MST
